import React from "react"

export default class PhoneVerify extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      code: ""
    }
  }

  onCodeChange(e) {
    this.setState({
      code: e.target.value
    })
  }

  verifyCode() {
    var that = this;
    window.confirmationResult.confirm(this.state.code)
      .then(function (result) {
        that.props.history.push("/home")
      }).catch(function (error) {
        that.props.history.push("/")
      });

  }

  render() {
    return (
      <div id="PhoneVerify">
        {
          this.state.error &&
          <div>
            Error: {this.state.err_msg}
          </div>
        }
        <input type="email" placeholder="Verification Code" onChange={this.onCodeChange.bind(this)}></input>
        <button id="btn" onClick={this.verifyCode.bind(this)}>Submit</button>
      </div>
    )
  }
}
