          .then(function (confirmationResult) {
            console.log("Sent");
            window.confirmationResult = confirmationResult;
          }).catch(function (error) {
            console.log(error);
          });
      }
    });

    var appVerifier = window.recaptchaVerifier;


  }
  render() {
    return (
      <div className="App">
        <button id="btn">Submit</button>
      </div>
    );
  }
}

export default App;
