    }).catch(function (error) {
      console.log("Not sent");
      console.log(error);
    });
  }

  verifyPhoneNumber() {

  }

  componentDidMount() {
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        console.log("Callback");
      }
    });
  }

  render() {
    return (
      <div className="App">
        <button id="btn" onClick={sendPhoneNumber()}>Submit</button>
      </div>
    );
  }
}

export default App;
