import React from 'react';
import axios from 'axios'
import firebase from 'firebase/app';

import "./setupProxy"
import 'firebase/auth'
import './App.css';

const firebaseConfig = {
  apiKey: "AIzaSyDiHmms0XCJRHhzMzUJDUz1G1NwLNWbRjM",
  authDomain: "csi4139-lab2.firebaseapp.com",
  databaseURL: "https://csi4139-lab2.firebaseio.com",
  projectId: "csi4139-lab2",
  storageBucket: "",
  messagingSenderId: "922993488222",
  appId: "1:922993488222:web:4f341d6d1e0a223c3f1fcf"
};

firebase.initializeApp(firebaseConfig);

class App extends React.Component {
  sendPhoneNumber(phoneNumber) {
    var phoneNumber = this.fetchPhoneNumber(phoneNumber).then(response => {
      console.log(response.data);
      firebase.auth().signInWithPhoneNumber(response.data, window.recaptchaVerifier)
        .then(function (confirmationResult) {
          console.log("Sent");
          window.confirmationResult = confirmationResult;
        }).catch(function (error) {
          console.log("Not sent");
          console.log(error);
        });
    })
  }

  fetchPhoneNumber(email) {
    return axios.get('/verifyEmail', {
      params: {
        email: email
      }
    });
  }

  componentDidMount() {
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        console.log("Callback");
      }
    });
  }

  render() {
    this.fetchPhoneNumber("HelloWorld")
    return (
      <div className="App">
        <input type="email" placeholder="email"></input>
        <button id="btn" onClick={this.sendPhoneNumber.bind(this, "HelloWorld")}>Submit</button>
      </div>
    );
  }
}

export default App;
