import React from "react"

export default class PhoneVerify extends React.Component {
  constructor(props) {
    super(props);
    this.onPhonenumberChange = props.onPhonenumberChange;
  }

  sendPhoneNumber() {
    axios.get('/verifyEmail', {
      params: {
        email: this.state.email
      }
    }).then(res => {
      firebase.auth().signInWithPhoneNumber("+1"+res.data, window.recaptchaVerifier)
      .then(function (confirmationResult) {
        console.log("Sent");
        window.confirmationResult = confirmationResult;
      }).catch(function (error) {
        console.log("Not sent");
        console.log(error);
      });
    }).catch(err => {
      console.error(err);
    })
  }


  render() {
    return (
      <div id="PhoneVerify">
        <input type="email" placeholder="Verification Code" onChange={this.onPhonenumberChange.bind(this)}></input>
        <button id="btn" onClick={this.sendPhoneNumber.bind(this)}>Submit</button>
      </div>
    )
  }
}
