import React from 'react';
import logo from './logo.svg';
import firebase from 'firebase/app';
import 'firebase/auth'
import './App.css';

const firebaseConfig = {
  apiKey: "AIzaSyDiHmms0XCJRHhzMzUJDUz1G1NwLNWbRjM",
  authDomain: "csi4139-lab2.firebaseapp.com",
  databaseURL: "https://csi4139-lab2.firebaseio.com",
  projectId: "csi4139-lab2",
  storageBucket: "",
  messagingSenderId: "922993488222",
  appId: "1:922993488222:web:4f341d6d1e0a223c3f1fcf"
};

firebase.initializeApp(firebaseConfig);





class App extends React.Component {
  componentDidMount() {
    var phoneNumber = "289-808-7783";

    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        alert("Callback");
        firebase.auth().signInWithPhoneNumber(phoneNumber, appVerifier)
        .then(function (confirmationResult) {
          // SMS sent. Prompt user to type the code from the message, then sign the
          // user in with confirmationResult.confirm(code).

          window.confirmationResult = confirmationResult;
        }).catch(function (error) {
          console.log(error);

          // Error; SMS not sent
          // ...
        });
      }
    });

    var appVerifier = window.recaptchaVerifier;


  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
        </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
        </a>
          <button id="btn"></button>
        </header>
      </div>
    );
  }
}

export default App;
