import React from "react"
import axios from 'axios'
import firebase from 'firebase/app';

import "./setupProxy"
import 'firebase/auth'
import './App.css';

import {Link} from 'react-router-dom'

import Loading from "./loading.gif"

export default class EmailInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      error: false,
      err_msg: "",
      loading: false,
      step: 1
    }
  }

  onPhonenumberChange(e) {
    this.setState({
      email: e.target.value
    })
  }

  componentDidMount() {
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        console.log("Callback");
      }
    });
  }

  sendPhoneNumber() {
    this.setState({
        loading: true
    })
    var that = this;
    axios.get('/api/sendCode', {
      params: {
        email: this.state.email
      }
    }).then(res => {
      that.setState({
        step: 2
      })
      console.log(res);
    }).catch(err => {
      that.setState({
        error: true,
        loading: false,
        err_msg: "Unable to find user."
      })
    })
  }

  render() {
    return (
      <div id="EmailInput" className="floating-form">
        {
          this.state.loading &&
          <div className="loader">
            <img src={Loading} />
          </div>
        }
        {
          this.state.error &&
          <div className="error">
            Error: {this.state.err_msg}
          </div>
        }
        <input name="email" type="email" placeholder="Email" onChange={this.onPhonenumberChange.bind(this)}></input>
        {
          this.state.step === 2 &&
          <div className="error">
            Error: {this.state.err_msg}
          </div>
        }
        <button id="btn" onClick={this.sendPhoneNumber.bind(this)}>Submit</button>
        <br />
        <br />
        <Link to="/register">Create User</Link>
      </div>
    )
  }
}
