import React from "react"

export default class PhoneVerify extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      code: ""
    }
  }

  onCodeChange(e) {
      console.log(e.target)
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  verifyCode() {
    var that = this;
    window.confirmationResult.confirm(this.state.code)
    .then(function (result) {
      that.props.history.push("/home")
    }).catch(function (error) {
      that.props.history.push("/")
    });

  }

  render() {
    return (
      <div id="PhoneVerify">
        <input name="email" placeholder="Email" onChange={this.onCodeChange.bind(this)}></input>
        <input name="name" placeholder="Full Name" onChange={this.onCodeChange.bind(this)}></input>
        <input name="phonenumber" placeholder="Phone Number" onChange={this.onCodeChange.bind(this)}></input>
        <button id="btn" onClick={this.verifyCode.bind(this)}>Submit</button>
      </div>
    )
  }
}
