import React from 'react';
import axios from 'axios'
import firebase from 'firebase/app';

import "./setupProxy"
import 'firebase/auth'
import './App.css';

const firebaseConfig = {
  apiKey: "AIzaSyDiHmms0XCJRHhzMzUJDUz1G1NwLNWbRjM",
  authDomain: "csi4139-lab2.firebaseapp.com",
  databaseURL: "https://csi4139-lab2.firebaseio.com",
  projectId: "csi4139-lab2",
  storageBucket: "",
  messagingSenderId: "922993488222",
  appId: "1:922993488222:web:4f341d6d1e0a223c3f1fcf"
};

firebase.initializeApp(firebaseConfig);

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: ""
    }
  }

  onPhonenumberChange(e) {
    this.setState({
      email: e.target.value
    })
    console.log(this.state)
  }

  sendPhoneNumber(email) {
    axios.get('/verifyEmail', {
      params: {
        email: email
      }
    }).then(res => {
      firebase.auth().signInWithPhoneNumber(res.data, window.recaptchaVerifier)
      .then(function (confirmationResult) {
        console.log("Sent");
        window.confirmationResult = confirmationResult;
      }).catch(function (error) {
        console.log("Not sent");
        console.log(error);
      });
    }).error(err => {
      console.error(err);
    })
  }

  componentDidMount() {
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        console.log("Callback");
      }
    });
  }

  render() {
    return (
      <div className="App">
        <input type="email" placeholder="email" onChange={this.onPhonenumberChange.bind(this)}></input>
        <button id="btn" onClick={this.sendPhoneNumber.bind(this, this.state.email)}>Submit</button>
      </div>
    );
  }
}

export default App;
