import React from "react"
import axios from "axios";

export default class PhoneVerify extends React.Component {
    constructor(props) {
        super(props);
        this.state = {}
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state)
    }

    createNewUser() {
        axios.post('/api/user', {
            firstName: 'Fred',
            lastName: 'Flintstone'
        })
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        return (
            <div id="PhoneVerify">
                <input name="email" placeholder="Email" onChange={this.onChange.bind(this)}></input>
                <input name="name" placeholder="Full Name" onChange={this.onChange.bind(this)}></input>
                <input name="phonenumber" placeholder="Phone Number" onChange={this.onChange.bind(this)}></input>
                <button id="btn" onClick={this.createNewUser.bind(this)}>Submit</button>
            </div>
        )
    }
}
