import React from "react"
import axios from "axios";
import {Link} from 'react-router-dom'

export default class PhoneVerify extends React.Component {
  constructor(props) {
    super(props);
    this.state = {error: false, err_msg: ""}
  }

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
    console.log(this.state)
  }

  createNewUser() {
    var that = this;
    axios.post('/api/user', null, {params: this.state})
      .then(function (response) {
        console.log(response);
        that.props.history.push("/")
      })
      .catch(function (error) {
        if (error.response.status === 503) {
          that.setState({
            error: true,
            err_msg: "Email already exists."
          })
        }
      });
  }

  render() {
    return (
      <div id="PhoneVerify" className="floating-form">
        {
          this.state.error &&
          <div>
            Error: {this.state.err_msg}
          </div>
        }
        <input name="email" placeholder="Email" onChange={this.onChange.bind(this)}></input>
        <input name="name" placeholder="Full Name" onChange={this.onChange.bind(this)}></input>
        <input name="phonenumber" placeholder="Phone Number" onChange={this.onChange.bind(this)}></input>
        <button id="btn" onClick={this.createNewUser.bind(this)}>Submit</button>
        <br />
        <br />
        <Link to="/register">Create User</Link>
      </div>
    )
  }
}
