import React from "react"
import axios from 'axios'
import firebase from 'firebase/app';

import "./setupProxy"
import 'firebase/auth'
import './App.css';

export default class EmailInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      error: false,
      err_msg: ""
    }
  }

  onPhonenumberChange(e) {
    this.setState({
      email: e.target.value
    })
  }

  componentDidMount() {
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('btn', {
      'size': 'invisible',
      'callback': function (response) {
        console.log("Callback");
      }
    });
  }

  sendPhoneNumber() {
    var that = this;
    axios.get('/api/user', {
      params: {
        email: this.state.email
      }
    }).then(res => {
      firebase.auth().signInWithPhoneNumber("+1" + res.data, window.recaptchaVerifier)
        .then(function (confirmationResult) {
          console.log("Sent");
          console.log(that.props.history.push("/verify"));
          window.confirmationResult = confirmationResult;
        }).catch(function (error) {
          that.setState({
            error: true,
            err_msg: "Unable to send text message."
          })
        });
    }).catch(err => {
      console.error(err);
    })
  }

  render() {
    return (
      <div id="EmailInput">
        {
          this.state.error &&
          <div>
            Error: {this.state.err_msg}
          </div>
        }
        <div id="Loader"></div>
        <input type="email" placeholder="email" onChange={this.onPhonenumberChange.bind(this)}></input>
        <button id="btn" onClick={this.sendPhoneNumber.bind(this)}>Submit</button>
      </div>
    )
  }
}
